package CS121;

public class Average {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 4;
		int num2 = 5;
		int num3 = 7;
		double average = 0.0;
		average = (num1+num2+num3)/3.0;
		
		System.out.println("the average of ("+num1 +" + "+num2+ " + "+num3 + ") / 3 = "+average);

	}//end of main

}//end of class
